import React, { useState } from "react";
import { ResultCard } from "./ResultCard";

export const Add = () => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);

  const [mediaType, setMediaType] = useState("movie");

  const onChange = (e) => {
    e.preventDefault();

    const currentMedia = e.target.value;
    setQuery(currentMedia);

    let url = "";

    switch (mediaType) {
      case "movie":
        url = `https://api.themoviedb.org/3/search/movie?api_key=${process.env.REACT_APP_TMDB_KEY}&language=en-US&page=1&include_adult=false&query=${currentMedia}`;
        break;
      // case "book":
      //   url = `https://www.googleapis.com/books/v1/volumes?q=${searchTerm}&key=${process.env.REACT_APP_BOOKS_KEY}`;
      //   break;
      // case "manga":
      //   url = `https://api.jikan.moe/v4/anime?q=${currentMedia}`;
      //   break;
      case "tv":
        url = `https://api.themoviedb.org/3/search/tv?api_key=${process.env.REACT_APP_TMDB_KEY}&language=en-US&page=1&query=${currentMedia}`;
        break;
      // case "game":
      //   url = `https://api.rawg.io/api/games?key=${process.env.REACT_APP_RAWG_KEY}&search=${searchTerm}`;
      //   break;
      default:
        return;
    }

    fetch(url)
      .then((res) => res.json())
      .then((data) => {
        if (data && !data.errors) {
          if (mediaType === "movie") {
            setResults(data.results);
          } else if (mediaType === "book") {
            setResults(data.items);
          } else if (mediaType === "manga") {
            setResults(data.results);
          } else if (mediaType === "tv") {
            setResults(data.results);
          } else if (mediaType === "game") {
            setResults(data.results);
          } else {
            setResults(data.results);
          }
        } else {
          setResults([]);
        }
      });
  };

  return (
    <div className="add-page">
      <div className="container">
        <div className="add-content">
          <div className="input-wrapper">
            <select
              value={mediaType}
              onChange={(e) => setMediaType(e.target.value)}
            >
              <option value="movie"> Movies </option>
              <option value="anime"> Anime </option>
              <option value="book"> Books </option>
              <option value="manga"> Manga </option>
              <option value="tv"> TV Shows </option>
              <option value="game"> Video Games </option>
            </select>{" "}
            <br /> <br />
            <input
              type="text"
              placeholder="Search for Anything..."
              value={query}
              onChange={onChange}
            />
          </div>

          {results.length > 0 && (
            <ul className="results">
              {" "}
              {results.map((movie) => (
                <li key={movie.id}>
                  {" "}
                  <ResultCard movie={movie} />
                </li>
              ))}{" "}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
};
